interface EditNameDialogProps {
  project: Project | null;
  open: boolean;
  onOpenChange: (open: boolean) => void;
}
```

## Implementation
```typescript
// src/components/project-card/EditNameDialog.tsx
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useUpdateProject } from "@/lib/hooks/useProjects";
import { useState, useEffect } from "react";

export function EditNameDialog({ project, open, onOpenChange }: EditNameDialogProps) {
  const [name, setName] = useState("");
  const [error, setError] = useState<string | null>(null);
  const updateProject = useUpdateProject();

  // Initialize with current name when dialog opens
  useEffect(() => {
    if (project && open) {
      // Use custom name if set, otherwise show derived name as placeholder
      setName(project.name || "");
      setError(null);
    }
  }, [project, open]);

  const handleSave = async () => {
    if (!project) return;

    setError(null);
    try {
      await updateProject.mutateAsync({
        id: project.id,
        name: name.trim() || null,  // Empty string â†’ null (use derived)
      });
      onOpenChange(false);
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to update name");
    }
  };

  const derivedName = project?.path.split("/").pop() || "";

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Edit Project Name</DialogTitle>
        </DialogHeader>

        <div className="space-y-4 py-4">
          <div className="space-y-2">
            <Label htmlFor="name">Display Name</Label>
            <Input
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder={derivedName}
            />
            <p className="text-xs text-muted-foreground">
              Leave empty to use folder name ({derivedName})
            </p>
          </div>

          {error && (
            <p className="text-sm text-destructive">{error}</p>
          )}
        </div>

        <DialogFooter>
          <Button variant="outline" onClick={() => onOpenChange(false)}>
            Cancel
          </Button>
          <Button 
            onClick={handleSave}
            disabled={updateProject.isPending}
          >
            {updateProject.isPending ? "Saving..." : "Save"}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}
