```typescript
// src/components/project-card/RelocateDialog.tsx
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { FolderBrowser } from "@/components/folder-browser/FolderBrowser";
import { Breadcrumbs } from "@/components/folder-browser/Breadcrumbs";
import { useUpdateProject } from "@/lib/hooks/useProjects";
import { useFilesystem } from "@/lib/hooks/useFilesystem";
import { AlertTriangle } from "lucide-react";
import { useState, useEffect } from "react";
import path from "path";

interface RelocateDialogProps {
  project: Project | null;
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function RelocateDialog({ project, open, onOpenChange }: RelocateDialogProps) {
  const [currentPath, setCurrentPath] = useState<string | undefined>();
  const [error, setError] = useState<string | null>(null);

  const updateProject = useUpdateProject();
  const { data: filesystem, isLoading } = useFilesystem(currentPath);

  // Set initial path when dialog opens
  useEffect(() => {
    if (project && open) {
      // Try to start at parent of old path
      // API will fall back to ~/dev or ~ if parent doesn't exist
      const parentPath = project.path.split("/").slice(0, -1).join("/");
      setCurrentPath(parentPath || undefined);
      setError(null);
    }
  }, [project, open]);

  const handleRelocate = async () => {
    if (!project || !filesystem?.currentPath) return;

    setError(null);
    try {
      await updateProject.mutateAsync({
        id: project.id,
        path: filesystem.currentPath,
      });
      onOpenChange(false);
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to relocate project");
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-lg max-h-[80vh] flex flex-col p-0">
        <DialogHeader className="px-4 py-3 border-b">
          <DialogTitle>Relocate Project</DialogTitle>
        </DialogHeader>

        {/* Old path context */}
        {project && (
          <div className="px-4 py-3 bg-secondary/30 border-b">
            <div className="flex items-center gap-2 text-sm text-muted-foreground">
              <AlertTriangle className="h-4 w-4 text-warning" />
              <span>Current path (missing):</span>
            </div>
            <p className="text-sm font-mono mt-1">{project.displayPath}</p>
          </div>
        )}

        {/* Breadcrumbs */}
        {filesystem && (
          <Breadcrumbs
            currentPath={filesystem.currentPath}
            displayPath={filesystem.displayPath}
            onNavigate={setCurrentPath}
            parent={filesystem.parent}
            canGoUp={filesystem.canGoUp}
          />
        )}

        {/* Directory List */}
        <div className="flex-1 overflow-y-auto min-h-[200px]">
          <FolderBrowser
            currentPath={currentPath}
            onNavigate={setCurrentPath}
          />
        </div>

        {/* Error */}
        {error && (
          <div className="px-4 py-2 text-sm text-destructive border-t">
            {error}
          </div>
        )}

        {/* Actions */}
        <div className="flex justify-end gap-2 px-4 py-3 border-t">
          <Button variant="outline" onClick={() => onOpenChange(false)}>
            Cancel
          </Button>
          <Button 
            onClick={handleRelocate}
            disabled={!filesystem?.currentPath || updateProject.isPending}
          >
            {updateProject.isPending ? "Updating..." : "Update Location"}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}
```

## Error Handling
The PATCH endpoint handles validation:
- **404**: New path doesn't exist â†’ show error, stay in dialog
- **400**: New path is not a directory â†’ show error
- **403**: New path outside home â†’ show error
- **409**: New path already tracked by another project â†’ show error

Show all errors inline, keep dialog open for retry.

